/**
 * principal.js
 * 
 * Este archivo define el componente Principal.
 * 
 * ¿Qué hace este componente?
 * - Muestra la pantalla principal de la aplicación después de que el usuario ingresa su nombre.
 * - Permite al usuario seleccionar el método de entrada de datos.
 * - Muestra los datos generados o ingresados.
 * - Permite calcular intervalos de confianza.
 * - Muestra visualizaciones y paneles de información.
 * - Permite cambiar de usuario o reiniciar la aplicación.
 */

import React, { useState } from "react";
// Importa aquí tus otros componentes en español si los tienes
// import SelectorEntradaDatos from "./SelectorEntradaDatos";
// import CalculadoraConfianza from "./CalculadoraConfianza";
// import PanelVisualizacion from "./PanelVisualizacion";
// import PanelInformacion from "./PanelInformacion";
// import MostrarDatos from "./MostrarDatos";

const Principal = ({
  nombreUsuario,
  alEnviarDatos,
  alCambiarUsuario,
  alReiniciar,
}) => {
  // Estado para saber si los datos están listos
  const [datosListos, setDatosListos] = useState(false);
  // Estado para guardar los datos estadísticos
  const [estadisticas, setEstadisticas] = useState(null);

  /**
   * Maneja cuando los datos están listos para ser usados.
   */
  const manejarDatosListos = (datos) => {
    setEstadisticas(datos);
    setDatosListos(true);
  };

  /**
   * Reinicia los datos y las estadísticas.
   */
  const manejarReiniciarDatos = () => {
    setDatosListos(false);
    setEstadisticas(null);
  };

  // Si los datos aún no están listos, muestra el selector de entrada de datos
  if (!datosListos) {
    return (
      // Contenedor principal con fondo personalizado
      <div
        className="min-h-screen bg-gray-50"
        style={{
          backgroundImage:
            "url('https://4tsix0yujj.ufs.sh/f/2vMRHqOYUHc0om3zHsOgDjdvqUQH6XhKYIiaSc3LCtrM1fen')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundAttachment: "fixed",
          backgroundBlendMode: "overlay",
          backgroundColor: "rgba(255, 255, 255, 0.9)",
        }}
      >
        {/* Aquí iría el componente para seleccionar el método de entrada de datos */}
        {/* <SelectorEntradaDatos
          nombreUsuario={nombreUsuario}
          alDatosListos={manejarDatosListos}
          alVolver={alCambiarUsuario}
        /> */}
        <div className="flex items-center justify-center h-screen">
          <h2 className="text-2xl font-bold text-gray-700">
            Aquí va el Selector de Entrada de Datos
          </h2>
        </div>
      </div>
    );
  }

  // Si los datos están listos, muestra la página principal con los paneles
  return (
    // Contenedor principal con fondo personalizado
    <div
      className="min-h-screen bg-gray-50"
      style={{
        backgroundImage:
          "url('https://4tsix0yujj.ufs.sh/f/2vMRHqOYUHc0om3zHsOgDjdvqUQH6XhKYIiaSc3LCtrM1fen')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundAttachment: "fixed",
        backgroundBlendMode: "overlay",
        backgroundColor: "rgba(255, 255, 255, 0.9)",
      }}
    >
      {/* Contenedor central de la aplicación */}
      <div className="max-w-4xl mx-auto py-8 px-4">
        {/* Encabezado con el nombre del usuario y botones */}
        <header className="mb-8 bg-white rounded-xl shadow-lg p-6 backdrop-blur-sm bg-opacity-90 border border-gray-200">
          <div className="flex justify-between items-center">
            {/* Información del usuario */}
            <div>
              <h1 className="text-3xl font-bold text-gray-800 font-serif">
                Intervalos De ConfianzaPro
              </h1>
              <p className="text-gray-600 mt-1">
                Usuario:{" "}
                <span className="font-medium text-blue-600">{nombreUsuario}</span>
              </p>
            </div>
            {/* Botones para cambiar usuario o reiniciar */}
            <div className="flex gap-3">
              <button
                onClick={alCambiarUsuario}
                className="text-sm bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg transition-colors shadow-md"
              >
                Cambiar usuario
              </button>
              <button
                onClick={alReiniciar}
                className="text-sm bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg transition-colors shadow-md"
              >
                Reiniciar todo
              </button>
            </div>
          </div>
        </header>

        {/* Contenido principal de la página */}
        <main className="space-y-6">
          {/* Mostrar los datos generados o ingresados */}
          {/* <MostrarDatos datos={estadisticas.datosGenerados} /> */}
          <div className="bg-white rounded-lg shadow p-4">
            <h3 className="text-lg font-semibold mb-2">Aquí se muestran los datos generados</h3>
          </div>

          {/* Calculadora de intervalos de confianza */}
          {/* <CalculadoraConfianza
            mediaInicial={estadisticas.media}
            desviacionInicial={estadisticas.desviacion}
            tamanoMuestraInicial={estadisticas.tamanoMuestra}
            esAutoGenerado={!!estadisticas.datosGenerados}
          /> */}
          <div className="bg-white rounded-lg shadow p-4">
            <h3 className="text-lg font-semibold mb-2">Aquí va la Calculadora de Intervalos de Confianza</h3>
          </div>

          {/* Panel de visualización de los intervalos */}
          {/* <PanelVisualizacion intervalo={...} /> */}
          <div className="bg-white rounded-lg shadow p-4">
            <h3 className="text-lg font-semibold mb-2">Aquí va el Panel de Visualización</h3>
          </div>

          {/* Panel de información adicional */}
          {/* <PanelInformacion /> */}
          <div className="bg-white rounded-lg shadow p-4">
            <h3 className="text-lg font-semibold mb-2">Aquí va el Panel de Información</h3>
          </div>

          {/* Botón para realizar una nueva prueba */}
          <div className="flex gap-4 justify-center">
            <button
              onClick={manejarReiniciarDatos}
              className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-8 rounded-lg transition-colors shadow-md flex items-center gap-2"
            >
              {/* Icono de reinicio */}
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-5 w-5"
                viewBox="0 0 20 20"
                fill="currentColor"
              >
                <path
                  fillRule="evenodd"
                  d="M4 2a1 1 0 011 1v2.101a7.002 7.002 0 0111.601 2.566 1 1 0 11-1.885.666A5.002 5.002 0 005.999 7H9a1 1 0 010 2H4a1 1 0 01-1-1V3a1 1 0 011-1zm.008 9.057a1 1 0 011.276.61A5.002 5.002 0 0014.001 13H11a1 1 0 110-2h5a1 1 0 011 1v5a1 1 0 11-2 0v-2.101a7.002 7.002 0 01-11.601-2.566 1 1 0 01.61-1.276z"
                  clipRule="evenodd"
                />
              </svg>
              Nueva prueba
            </button>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Principal;
